    // java.log(JSON.stringify(novels))
    // è¿”å›ç©ºåˆ—è¡¨ä¸­æ­¢æµç¨‹
    if (novels.length === 0) {
        return []
    }
    return novelFilter(util.formatNovels(util.handNovels(novels)))
})()

