function cacheGetAndSet(cache, key, supplyFunc) {
    let v = cache.get(key)
    if (v === undefined || v === null) {
        v = JSON.stringify(supplyFunc())
        cache.put(key, v, 7*24*60*60)  // å»¶é•¿ç¼“å­˜æ—¶é—´è‡³7å¤©
    }
    return JSON.parse(v)
}
