        sleepToast(`✅ 已取消屏蔽【${novel.userName}】\n\n现已恢复显示其小说`)
    } else if (novel.userId !== undefined && novel.userId !== null) {
        authors.push(Number(novel.userId))
        sleepToast(`✅ 已屏蔽【${novel.userName}】(本地)\n\n今后不再显示其小说`)
