        return handlerRanking()
    }
    if (baseUrl.includes("/editors_picks")) {
        return handlerRanking()
    }
    if (baseUrl.includes("https://www.pixiv.net")) {
        return handlerRanking()
    }
    else {
        return []
    }
}

