jsLib:
function getChapterList() {
  var content = java.getPage();

  // 检查是否有章节列表
  if (!java.exists('#jieqi_page_contents')) {
    java.toast('章节列表为空，尝试重新加载');
    java.refresh();
    return [];
  }

  // 直接获取所有章节元素
  var chapters = java.getElements('#jieqi_page_contents dd');

  // 如果章节列表为空，尝试备用选择器
  if (chapters.length === 0) {
    chapters = java.getElements('.chapterlist dd');
  }

  var result = [];
  for (var i = 0; i < chapters.length; i++) {
    var chapter = chapters[i];
    var name = java.getString('a@text', chapter);
    var url = java.getString('a@href', chapter);

    if (name && url) {
      result.push({
        name: name,
        url: url
      });
    }
  }

  // 如果仍然为空，尝试分页加载
  if (result.length === 0) {
    var nextPage = java.getString('a:matches(下一页)@href');
    if (nextPage) {
      java.setPage(java.get(nextPage).body());
      return getChapterList();
    }
  }

  return result;
}

