function show() {
    let jc = source.getLoginHeader();
    let us, id, codes
    if (jc) {
        let info = jc.match(/%22%3A%22(\d+?)%22%2C%22username%22%3A%22(.*?)%22%7D/)
        us = result['用户名/邮箱'];
        id = info[1];
        codes = "✔️已登录";
    } else {
        us = "无";
        id = "无";
        codes = "❌未登录";
    }
    let txt = `\n◎用户：${us}◎\n◎用户ID：${id}◎\n◎登录状态：${codes}◎`
    java.longToast(txt);
}

