script@all
<js>
var na=source.bookSourceComment;
var ra=java.base64Decode('QnnlsI/mlrAK6aaW5Y+R6K665Z2bOmh0dHBzOi8vc3JjYmJzLmNvbS8=');
if(na.includes(ra)){
let url=result.match(/video.src = '(https.*\.m3u8)';/)[1];
//java.log(url);
var img=java.get("pic")
var p = url;
    let name = chapter.title;
    if (book['durChapterIndex'] == chapter['index']) {
      b64 = java['base64Encode'](`
    <html>
    <head>
        <meta name="viewport" content="width=device-width">
        <style>
            body {
                margin: 0;
                font-family: Arial, sans-serif;
                background: linear-gradient(135deg, #ece9e6, #ffffff);
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: flex-start;
                padding: 20px;
            }
            .container {
                width: 100%;
                max-width: 800px;
                background: #fff;
                border-radius: 10px;
                box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
                overflow: hidden;
                margin-bottom: 20px;
            }
            h3 {
                margin: 0;
                padding: 10px 20px;
                font-size: 0.9em;
                background: #333;
                color: #fff;
                border-radius: 10px 10px 0 0;
            }
            .video-container {
                width: 100%;
                background: #000;
                position: relative;
            }
            video {
                width: 100%;
                height: auto;
                display: block;
            }
            .info-container {
                padding: 20px;
            }
            .info-container p {
                margin: 10px 0;
                color: #555;
            }
            .info-container p span {
                font-weight: bold;
                color: #000;
            }
        </style>
    </head>
    <body>
        <div class="container">
            <h3>${name}</h3>
            <div class="video-container">
                <video id="video" src="${p}"poster="${img}"  controls></video>
            </div>
        </div>

        <script>
            // 获取视频元素
            const video = document.getElementById('video');
            let touchStartX = 0;
            let touchStartY = 0;
            let touchStartTime = 0;
            let isLongPress = false;

            // 滑动快进
            video.addEventListener('touchstart', (e) => {
                touchStartX = e.touches[0].clientX;
                touchStartY = e.touches[0].clientY;
                touchStartTime = Date.now();
                isLongPress = false;
            });

            video.addEventListener('touchmove', (e) => {
                const deltaX = e.touches[0].clientX - touchStartX;
                const deltaY = e.touches[0].clientY - touchStartY;

                // 如果是水平滑动，且未进入长按状态
                if (Math.abs(deltaX) > Math.abs(deltaY) && !isLongPress) {
                    const duration = video.duration;
                    const seekTime = video.currentTime + (deltaX / 50); // 每滑动100px快进1秒
                    video.currentTime = Math.min(Math.max(seekTime, 0), duration);
                }
                e.preventDefault(); // 防止默认行为（如页面滚动）
            });

            // 长按快进
            video.addEventListener('touchend', (e) => {
                const touchEndTime = Date.now();
                const touchDuration = touchEndTime - touchStartTime;

                // 如果长按时间超过500ms，进入长按快进状态
                if (touchDuration > 500) {
                    isLongPress = true;

                    // 在长按状态下，根据滑动距离快进
                    const deltaX = e.changedTouches[0].clientX - touchStartX;
                    const duration = video.duration;
                    const seekTime = video.currentTime + (deltaX / 100); // 每滑动100px快进1秒
                    video.currentTime = Math.min(Math.max(seekTime, 0), duration);
                } else {
                    isLongPress = false;
                }
            });
        </script>
    </body>
    </html>`);  
    dataUrl = 'data:text/html;base64,' + b64;
      java['startBrowser'](dataUrl, chapter.title);
      java['toast']('正在加载视频\n视频加载较慢，请耐心等待');
    }
    '【刷新正文】播放\n✅直链:\n' + p;   
}
else{	java.base64Decode('6K+35LiN6KaB5L+u5pS55L2c6ICFLOivt+S4jeimgeebl+a6kA==');
	}
</js>

